package com.sri.shareurl.addurl.services;import java.io.IOException;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Service;import org.springframework.transaction.annotation.Transactional;import com.sri.shareurl.addurl.neo4j.entities.URL;import com.sri.shareurl.addurl.neo4j.entities.User;import com.sri.shareurl.addurl.neo4j.repo.URLRepository;import com.sri.shareurl.addurl.neo4j.repo.UserRepository;import com.sri.shareurl.addurl.utilities.TitleExtractor;@Servicepublic class URLServiceImpl implements IfcURLService {	@Autowired	private URLRepository urlRepository;	@Autowired	private UserRepository userRepository;	/*	 * (non-Javadoc)	 * 	 * @see	 * com.sri.shareurl.addurl.services.IfcUserService#addURL(java.lang.String,	 * java.lang.String, java.lang.String)	 */	@Override	@SuppressWarnings("deprecation")	@Transactional	public URL addURL(String username, String scope, String url)			throws IOException {		User user = userRepository.findByName(username);		URL urlSharing = new URL(TitleExtractor.getPageTitle(url), url);		urlRepository.save(urlSharing);		user.Sharing(urlSharing, scope);		user = userRepository.save(user);		return urlSharing;	}}